// -------------------- Funciones principales --------------------
export async function fetchFoods() {
  return _foods.slice().sort((a, b) => b.id - a.id);
}

export async function createFood(formData) {
  await new Promise((r) => setTimeout(r, 300));
  const name = (formData.get("name") || "").trim();
  const description = (formData.get("description") || "").trim();
  const price = parseFloat(formData.get("price") || "0") || 0;
  const is_available = String(formData.get("is_available")) === "true";
  const prep = parseInt(formData.get("prep_time_minutes") || "0", 10) || 0;

  const catId = formData.get("category_id");
  let category = null;
  if (catId) {
    const found = _categories.find(c => c.id === Number(catId));
    if (found) category = { id: found.id, name: found.name };
  }

  let photo_url = null;
  const file = formData.get("photo");
  if (file && typeof file !== "string") {
    photo_url = await fileToDataURL(file);
  }

  const created = {
    id: Math.max(0, ..._foods.map(f => f.id)) + 1,
    name,
    description,
    price,
    category,
    is_available,
    prep_time_minutes: prep,
    photo_url,
  };

  _foods.push(created);
  return created;
}

export async function deleteFood(id) {
  await new Promise((r) => setTimeout(r, 200));
  _foods = _foods.filter(f => f.id !== id);
  return { ok: true };
}

export async function toggleFoodAvailability(id) {
  const idx = _foods.findIndex(f => f.id === id);
  if (idx >= 0) {
    _foods[idx].is_available = !_foods[idx].is_available;
    return { ok: true, item: _foods[idx] };
  }
  return { ok: false };
}

// ğŸ‘‰ Agregar a tu API mock
let _nextCatId = 1000; // base alta para no chocar con ids existentes

export async function createCategory(name) {
  const n = String(name || "").trim();
  if (!n) throw new Error("Nombre de categorÃ­a vacÃ­o");

  // evitar duplicados (case-insensitive)
  const dup = _categories.find(c => c.name.toLowerCase() === n.toLowerCase());
  if (dup) return dup;

  const created = { id: _nextCatId++, name: n };
  _categories.push(created);
  return created;
}

// === PEDIDOS: localStorage fallback ===
const ORDERS_KEY = "pm_orders_v1";

function readOrdersLS() {
  try { return JSON.parse(localStorage.getItem(ORDERS_KEY)) || []; }
  catch { return []; }
}
function writeOrdersLS(list) {
  localStorage.setItem(ORDERS_KEY, JSON.stringify(list));
}

export async function createOrder(order) {
  // order: { items, subtotal, customer, note }
  const list = readOrdersLS();
  const id = crypto.randomUUID?.() || String(Date.now());
  const now = new Date().toISOString();
  const newOrder = {
    id,
    status: "pending", // pending | completed | canceled
    createdAt: now,
    updatedAt: now,
    ...order,
  };
  list.unshift(newOrder);
  writeOrdersLS(list);
  return newOrder;
}

export async function fetchOrders() {
  return readOrdersLS();
}

export async function updateOrderStatus(id, status) {
  const list = readOrdersLS();
  const i = list.findIndex(o => o.id === id);
  if (i === -1) throw new Error("Pedido no encontrado");
  list[i] = { ...list[i], status, updatedAt: new Date().toISOString() };
  writeOrdersLS(list);
  return list[i];
}

export async function deleteOrder(id) {
  const list = readOrdersLS().filter(o => o.id !== id);
  writeOrdersLS(list);
  return true;
}
